import React, { Component } from "react";
import NewsItem from "./NewsItem";

export class News extends Component {
  
  constructor() {
    super();
    console.log("Hello I am a constructor from News component");
    this.state = {
      articles:[],
      loading: false,
      page:1
    };
  }
  async componentDidMount()
  {
        let url = "https://newsapi.org/v2/top-headlines?country=us&apiKey=9890348979194aa78767c23d83dc0869&page=1"
        let data = await fetch(url);
        let parsedData = await data.json();
        console.log(parsedData)
        this.setState({articles: parsedData.articles})
  }

  render() {
    return (
      <div className="container my-4">
        <h2>NewsMonkey - Top Headlines</h2>
        <div className="row">
          {this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem title={element.title?element.title.slice(0,40):""} description={element.description?element.description.slice(0,88):""} imageUrl={element.urlToImage} newsUrl={element.url}/>
              </div>
            );
          })}
        </div>
        <div className="container">
            <button type="button" class="btn btn-dark">&larr; Previous</button>
            <button type="button" class="btn btn-dark">Next &rarr;</button>
        </div>
            

      </div>
    );
  }
}

export default News;
