import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
export class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      loading: false,
      page: 1,
    
    };
  }
  async componentDidMount() {
    let url =`https://newsapi.org/v2/top-headlines?country=us&apiKey=9890348979194aa78767c23d83dc0869&page=1&pageSize=${this.props.pageSize}`;
    let data = await fetch(url);
    this.setState({loading: true});
        let parsedData = await data.json()  
        this.setState({
            page: this.state.page - 1,
            articles: parsedData.articles,
            loading: false
        })

    }

  handleNextClick = async () => {
    if (this.state.page + 1 > Math.ceil(this.state.totalResults/20)){

    }
    else{
        let url =`https://newsapi.org/v2/top-headlines?country=us&apiKey=9890348979194aa78767c23d83dc0869&page=${this.state.page + 1}&pageSize=${this.props.pageSize}`;
        this.setState({loading: true});
        let data = await fetch(url);
        let parsedData = await data.json()
        
        this.setState({
            page: this.state.page - 1,
            articles: parsedData.articles,
            loading: false
        })

    }
}


  handlePrevClick = async () => {
    let url =`https://newsapi.org/v2/top-headlines?country=us&apiKey=9890348979194aa78767c23d83dc0869&page=${this.state.page - 1}&pageSize=${this.props.pageSize}`;
    this.setState({loading: true});
        let data = await fetch(url);
        let parsedData = await data.json() 
        this.setState({
            page: this.state.page - 1,
            articles: parsedData.articles,
            loading: false
        })

    }
    
  render() {
    return (
      <div className="container my-4">
        <h2>NewsMonkey - Top Headlines</h2>
        {this.state.loading && <Spinner/>}
        <div className="row">
          {!this.state.loading && this.state.articles.map((element) => {
            return (
              <div className="col-md-4" key={element.url}>
                <NewsItem
                  title={element.title ? element.title.slice(0, 40) : ""}
                  description={
                    element.description ? element.description.slice(0, 88) : ""
                  }
                  imageUrl={element.urlToImage}
                  newsUrl={element.url}
                />
              </div>
            );
          })}
        </div>
        <div className="container d-flex justify-content-between">
          <button
            type="button" disabled={this.state.page<=1}
            class="btn btn-dark"
            onClick={this.handlePrevClick}
          >
            &larr; Previous
          </button>
          <button
            disabled={this.state.page + 1 > Math.ceil(this.state.totalResults/20)}
            type="button"
            class="btn btn-dark"
            onClick={this.handleNextClick}
          >
            Next &rarr;
          </button>
        </div>
      </div>
    );
  }
}

export default News;
